  @media (max-width: 800px) {
    justify-content: space-between;
  }
  h1 {
    font-weight: 500;
  }
  .desktopNav {
    display: flex;
    list-style: none;
  }
  .desktopNav > li {
    padding: 10px 25px;
    text-transform: uppercase;
    cursor: pointer;
    :hover,
    :focus {
      opacity: 0.6;
    }
    @media (max-width: 1000px) {
      padding: 10px;
    }
  }
  @media screen and (max-width: 800px) {
    .desktopNav {
      display: block;
      position: fixed;
      background-color: #fff;
      top: 12%;
      left: 50%;
      transform: translateX(-50%);
      width: 90%;
      text-align: left;
      padding-right: 10px;
      opacity: 0;
      transition: all 0.3s ease-in-out;
    }
    .desktopNav li {
      margin: 0 auto;
      border-bottom: 1px solid #1821531b;
