import React from "react";
import styled from "styled-components";
import profile from "../assets/profile.webp";
import background from "../assets/background.jpg";
import data from "../data.json";
const Contactinfos = () => {
  return (
    <Container>
      <span className="color"/>
        <div className="container">
          {/* <img src={background} alt="" className="backImg"/> */}

          <img className="imgDiv" src={profile} alt="" />

          <div className="list">
            <ul>
              {" "}
              <p className="nameHeader"> Jamila Israfilzadeh</p>
              <li>Phone:</li>
              <p>{data.phone}</p>
              <li>Email:</li>
              <p>{data.email}</p>
              <li>Portfolio:</li>
              <p>{data.portfolio}</p>
              <li>LinkedIn:</li>
              <p>{data.linkedin}</p>
              {/* <li>Date of birth:</li>
              <p>March 3rd, 2000</p> */}
            </ul>
          </div>
        {/* </div> */}
      </div>
    </Container>
  );
};

export default Contactinfos;

const Container = styled.div`
  padding: 5%;
  position: relative;
  background-image: url(${background});
  background-size: cover;
  .container {
    padding: 3%;

    display: flex;
    align-items: center;
    margin: 0 auto;
    justify-content: center;
    /* background-image: url(${background}); */
    position: relative;
    z-index: 1;
  }
  .backImg {
    position: absolute;
    z-index: -1;
    filter: hue-rotate(300deg);
    /* height: 100%; */
  }

  .imgDiv {
    width: 50%;
    -webkit-filter: grayscale(100%); /* Safari 6.0 - 9.0 */
    filter: grayscale(100%);
  }
  .nameHeader {
    font-size: 25px;
    font-weight: 400;
    padding-bottom: 20px;
  }
  .list {
    width: 50%;
    background-color: #F1F1F1;
    padding: 3%;
    height: 100%;
  }
  ul {
    list-style: none;
    text-align: left;
    width: 100%;

    p {
      color: #2c2c2cce;
      cursor: pointer;
    }
  }
  ul > li {
    font-weight: 500;
    font-size: 15px;
  }
  .color {
    position: absolute;
    left: 0;
    top: 0;
    opacity: 0.8;
    width: 100%;
    height: 100%;
    background-color: #182153;
  }
`;
