import React from "react";
import styled from "styled-components";
import { experience } from "../mockData/mockData";

const Experience = () => {
  return (
    <div>
      <Container id="experience">
        <hr />
        <h1>Work Experience</h1>
        <ul>
          {experience.map((item) => (
            <>
              <div
                data-aos="fade-down"
                data-aos-duration="800"
                className="dot"
              ></div>
              <li>
                <h2 data-aos="fade-right" data-aos-duration="1200">
                  {item.time}
                  <img src={item.imgPath} alt="" />
                </h2>
                <p data-aos="fade-left" data-aos-duration="1200">
                  <span> {item.name}</span> {item.desc}
                </p>
              </li>{" "}
            </>
          ))}
        </ul>
      </Container>
    </div>
  );
};

export default Experience;
const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 20px;
  color: #182153;
  a {
    text-decoration: none;
  }
  hr {
    width: 100%;
    border: 0.1px solid #18215328;
  }
  h1 {
    font-weight: 500;
    font-size: 25px;
  }
  ul {
    width: 60%;
    list-style: none;
    .dot {
      width: 10px;
      height: 10px;
      border-radius: 10px;
      background-color: #182153;
      margin-left: -5px;
      display: flex;
    }
  }

  ul > li {
    display: flex;
    justify-content: space-between;
    align-items: top;
    font-size: 15px;
    line-height: 2;
    opacity: 0.9;
    border-left: 0.6px solid #1821532f;
    position: relative;
    h2 {
      font-weight: 500;
      font-size: 20px;
      white-space: nowrap;
      padding: 0 20px;
      display: flex;
      flex-direction: column;
      align-items: center;
      gap: 10px;
      width: 180px;
      img {
        width: 100px;
        height: 100px;
        object-fit: contain;
      }
    }
    span {
      font-weight: bold;
      color: #182153;
      display: block;
      padding: 10px 0;
    }
    p {
      text-align: left;
      padding-left: 20px;
      margin-left: 20px;
    }
  }
  @media screen and (max-width: 800px) {
    ul {
      width: 100%;
    }
    ul > li {
      display: flex;
      flex-direction: column;
    }
  }
`;
