import React from "react";
import styled from "styled-components";

const Experience = () => {
  return (
    <div>
      <Container>
        <h1>Experience</h1>
        <ul>
          <div className="dot"></div>
          <li>
            <h2>2019</h2>
            <p>
              <span>EUROPEAN YOUTH OLYMPİCS FESTİVAL(EYOF)</span>
              Although this field of experience was not exactly related to my
              specialization, it was a platform where I worked voluntarily for a
              few months. Here I assisted the German teams during the Olympic Games.
              It was a different and good experience for me.
            </p>
          </li>
          <div className="dot"></div>
          <li>
            <h2></h2>
            <p>
              <span>TASKILLED</span>
              In this project I have worked some part of project such as student profile and sign-Up&Login  pages.
            </p>
          </li>{" "}
          <div className="dot"></div>
          <li>
            <h2>03-7. 2020</h2>
            <p>
              <span>IBA TECH ACADEMY</span>I was accepted to this course as a
              result of an exam and we worked on many useful and instructive
              projects in the field of mobile application development. There are
              also some of these projects in my portfolio. In the same year, we
              finished the course with a successful final project that we did as
              a group business.
            </p>
          </li>
        </ul>
      </Container>
    </div>
  );
};

export default Experience;
const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 20px;
  color: #182153;
  h1 {
    font-weight: 500;
    font-size: 25px;
  }
  ul {
    width: 48%;
    list-style: none;
    .dot {
      width: 10px;
      height: 10px;
      border-radius: 10px;
      background-color: #182153;
      margin: 5px;
      margin-left: 26%;
      display: flex;
    }
  }
  ul > li {
    display: flex;
    justify-content: space-between;
    /* text-align: left; */
    align-items: top;
    font-size: 14px;
    line-height: 2;
    opacity: 0.9;
    h2 {
      font-weight: 500;
      font-size: 20px;
      white-space: nowrap;
      padding: 0 20px;
    }
    span {
      font-weight: 500;
      display: block;
      padding: 10px 0;
    }
    p {
      text-align: left;
      padding-left: 20px;
      margin-left: 20px;
      border-left: 0.6px solid #1821538f;
    }
  }
`;
