import React, { useEffect, useState } from "react";
import styled from "styled-components";
import info from "../data";

const Header = () => {
  function scrollToX(pathX) {
    document.querySelector(`#${pathX}`).scrollIntoView({
      behavior: "smooth",
    });
  }
  const [mobileMenuOpen, setMbileMenuOpen] = useState(false);

  useEffect(() => {
    if (window.innerWidth < 768) {
      setMbileMenuOpen(true);
    }
  }, []);

  return (
    <HeaderContainer>
      <h1>{info.fullName}</h1>

      <section className="top-nav">
        <input id="menu-toggle" type="checkbox" />
        <label className="menu-button-container" for="menu-toggle">
          <div className="menu-button"></div>
        </label>
      </section>

      <ul className="desktopNav">
        <li onClick={() => scrollToX("infos")}>Top</li>
        <li onClick={() => scrollToX("experience")}>Experience</li>
        <li onClick={() => scrollToX("volunteer")}>Volunteer </li>
        <li onClick={() => scrollToX("education")}>Education</li>
        <li onClick={() => scrollToX("skills")}>Skills</li>
        <li onClick={() => scrollToX("projects")}>Projects</li>
      </ul>
    </HeaderContainer>
  );
};

export default Header;

const HeaderContainer = styled.div`
  display: flex;
  align-items: center;
  width: 100%;
  z-index: 5;
  background-color: #fff;
  position: fixed;
  box-sizing: border-box;
  justify-content: space-around;

  @media (max-width: 800px) {
    /* width: initial; */
    /* margin: 10px auto !important; */
    /* width: 80%; */
  }
  h1 {
    font-weight: 500;
  }
  .desktopNav {
    display: flex;
    list-style: none;
  }
  .desktopNav > li {
    padding: 10px 25px;
    text-transform: uppercase;
    cursor: pointer;
    :hover,
    :focus {
      opacity: 0.6;
    }
  }
  @media screen and (max-width: 800px) {
    .desktopNav {
      display: none;
    }
  }
  @import url(https://fonts.googleapis.com/css?family=Raleway);
  h2 {
    vertical-align: center;
    text-align: center;
  }

  .top-nav {
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: space-between;
    /* background-color: #00baf0; */
    /* background: linear-gradient(to left, #f46b45, #eea849); */
