import React from "react";
import styled from "styled-components";
import info from "../data.json";
import { useScrollIntoView } from "@mantine/hooks";
// import { Link } from "react-router-dom";

const Header = () => {

  function scrollTo(){
    let x = document.getElementById("education")
    // window.scrollTo()
    loca
  }

  return (
    <HeaderContainer>
      <h1>{info.fullName}</h1>

      <ul>
        <li>Top</li>
        <li onClick={scrollTo}>Education</li>
        <li>Experience</li>
        <li>Skills</li>
        <li>Projects</li>
      </ul>
    </HeaderContainer>
  );
};

export default Header;

const HeaderContainer = styled.div`
  display: flex;
  align-items: center;
  width: 100%;
  
  z-index: 5;
  background-color: #fff;
  position: fixed;
  box-sizing: border-box;
  justify-content: space-around;
  h1 {
    font-weight: 400;
 
  }
  ul {
    display: flex;
    list-style: none;
  }
  ul > li {
    padding: 10px 25px;
    text-transform: uppercase;
    cursor: pointer;

    :hover,
    :focus {
      opacity: 0.6;
    }
  }
`;
