import React from "react";
import styled from "styled-components";
import ramLogo from "../assets/ramLogo.jpg";
import batiGoz from "../assets/batigozLogo.png";
import taskool from "../assets/taskoolLogo.jpg";
import azercell from "../assets/Azercell-Logo.wine.png";
import eyof from "../assets/eyof.jpeg";
import abb from "../assets/abbLogo.jpg";

const Experience = () => {
  return (
    <div>
      <Container id="experience">
        <hr />
        <h1>Work Experience</h1>
        <ul>
          <div
            data-aos="fade-down"
            data-aos-duration="800"
            className="dot"
          ></div>
          <li>
            <h2 data-aos="fade-right" data-aos-duration="1200">
              09.2022 - present
              <img src={ramLogo} alt="" />
            </h2>
            <p data-aos="fade-left" data-aos-duration="1200">
              <span>
                <span>RAM Holding </span> - Frontend developer
              </span>{" "}
              I was accepted as a frontend developer intern at RAM Holding in
              September 2022, and after gaining 3 months of experience, I was
              hired as a junior frontend developer. During my time there, I
              worked on various website development projects, including projects
              such as aqrotexnika.az and cec, which have now been successfully
              delivered. I am committed to continuously improving myself in this
              field.
            </p>
          </li>{" "}
          <div
            className="dot"
            data-aos="fade-down"
            data-aos-duration="800"
          ></div>
          <li>
            <h2 data-aos="fade-right" data-aos-duration="1200">
              07.2022 - 12.2022
              <img src={batiGoz} alt="" />
            </h2>
            <p data-aos="fade-left" data-aos-duration="1200">
              <span>Batıgöz clinic - Operator</span>I started working as an
              operator at Batıgöz Clinic to overcome my social awkwardness and
              learn to collaborate effectively in a team. It was a short-term
              opportunity in a different field, but it provided me with
              significant experience. I had the chance to work alongside
              colleagues and gain valuable insights. It was a rewarding
              experience that allowed me to expand my horizons and develop new
              skills.
            </p>
          </li>{" "}
          <div
            className="dot"
            data-aos="fade-down"
            data-aos-duration="800"
          ></div>
          <li>
            <h2 data-aos="fade-right" data-aos-duration="1200">
              12.2021 - 07.2022
              <img src={taskool} alt="" />
            </h2>
            <p data-aos="fade-left" data-aos-duration="1200">
              <span>
                TASKOOL - TASKILLED{" "}
                <a
                  href="http://www.taskool.com/"
                  rel="noreferrer"
                  target="_blank"
                >
                  {" "}
                  ( Taskool.com )
                </a>
              </span>
              In order to participate in the internship as a student in the last
              year of the university, I applied and was accepted for the
              position of frontend developer intern at Taskool, which I met on
              the Internet. Through this site, I easily grasped many of the
              topics I had difficulty learning by experimenting on a real
              project.
            </p>
          </li>{" "}
          <div
            className="dot"
            data-aos="fade-down"
            data-aos-duration="800"
          ></div>
        </ul>
      </Container>
    </div>
  );
};

export default Experience;
const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 20px;
  color: #182153;
  a {
    text-decoration: none;
  }
  hr {
    width: 100%;
    border: 0.1px solid #18215328;
  }
  h1 {
    font-weight: 500;
    font-size: 25px;
  }
  ul {
    width: 60%;
    list-style: none;
    .dot {
      width: 10px;
      height: 10px;
      border-radius: 10px;
      background-color: #182153;
      margin-left: -5px;
      display: flex;
    }
  }

  ul > li {
    display: flex;
    justify-content: space-between;
    align-items: top;
    font-size: 15px;
    line-height: 2;
    opacity: 0.9;
    border-left: 0.6px solid #1821532f;
    position: relative;
    h2 {
      font-weight: 500;
      font-size: 20px;
      white-space: nowrap;
      padding: 0 20px;
      display: flex;
      flex-direction: column;
      align-items: center;
      gap: 10px;
      width: 180px;
      img {
        width: 100px;
        height: 100px;
        object-fit: contain;
      }
    }
    span {
      font-weight: bold;
      color: #182153;
      display: block;
      padding: 10px 0;
    }
    p {
      text-align: left;
      padding-left: 20px;
      margin-left: 20px;
    }
  }
  @media screen and (max-width: 800px) {
    ul {
      width: 100%;
    }
    ul > li {
      display: flex;
      flex-direction: column;
    }
  }
`;
