import React, { useEffect, useState } from "react";
import styled from "styled-components";
import info from "../data";

const Header = () => {
  function scrollToX(pathX) {
    document.querySelector(`#${pathX}`).scrollIntoView({
      behavior: "smooth",
    });
  }
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

  const handleBurgerMenu = (e) => {
    setMobileMenuOpen(!mobileMenuOpen);
  };

  return (
    <HeaderContainer>
      <h1>{info.fullName}</h1>

      <section className="top-nav">
        <input id="menu-toggle" type="checkbox" onClick={handleBurgerMenu} />
        <label className="menu-button-container" htmlFor="menu-toggle">
          <div className="menu-button"></div>
        </label>
      </section>
      {mobileMenuOpen && (
        <ul className={`desktopNav ${mobileMenuOpen ? "active" : ""}`}>
          <li onClick={() => scrollToX("infos")}>Top</li>
          <li onClick={() => scrollToX("experience")}>Experience</li>
          <li onClick={() => scrollToX("volunteer")}>Volunteer </li>
          <li onClick={() => scrollToX("education")}>Education</li>
          <li onClick={() => scrollToX("skills")}>Skills</li>
          <li onClick={() => scrollToX("projects")}>Projects</li>
        </ul>
      )}
    </HeaderContainer>
  );
};

export default Header;

const HeaderContainer = styled.div`
  display: flex;
  align-items: center;
  width: 100%;
  z-index: 5;
  background-color: #fff;
  position: fixed;
  box-sizing: border-box;
  justify-content: space-around;
  @media (max-width: 800px) {
    /* width: initial; */
    /* margin: 10px auto !important; */
    /* width: 80%; */
  }
  h1 {
    font-weight: 500;
  }
  .desktopNav {
    display: flex;
    list-style: none;
  }
  .desktopNav > li {
    padding: 10px 25px;
    text-transform: uppercase;
    cursor: pointer;
    :hover,
    :focus {
      opacity: 0.6;
    }
    @media (max-width: 1000px) {
      padding: 10px;
    }
  }
  @media screen and (max-width: 800px) {
    .desktopNav {
      display: block;
      position: fixed;
      background-color: #fff;
      top: 12%;
      left: 50%;
      transform: translateX(-50%);
      width: 90%;
      text-align: left;
      padding-right: 10px;
      opacity: 0;
      transition: all 0.3s ease-in-out;
      li {
        margin: 0 auto;
        border-bottom: 1px solid #1821531b;
      }
    }
    .desktopNav.active {
      top: 12%;
      opacity: 1;
    }
  }
`;
