import React from "react";
import styled from "styled-components";
import info from "../data.json";
import { useScrollIntoView } from "@mantine/hooks";

const Header = () => {
  return (
    <HeaderContainer>
      <h1>{info.fullName}</h1>

      <ul>
        <li>Top</li>
        <li>Education</li>
        <li>Experience</li>
        <li>Skills</li>
        <li>Projects</li>
      </ul>
    </HeaderContainer>
  );
};

export default Header;

const HeaderContainer = styled.div`
  display: flex;
  align-items: center;
  width: 100%;
  z-index: 5;
  background-color: #fff;
  position: fixed;
  justify-content: space-between;
  h1 {
    font-weight: 500;
    padding: 20px;
  }
  ul {
    display: flex;
    list-style: none;
  }
  ul > li {
    padding: 10px 25px;
    text-transform: uppercase;
    cursor: pointer;

    :hover,
    :focus {
      opacity: 0.6;
    }
  }
`;
